import FetchWeather from "./FetchWeather";
import { useState } from "react";
import "./App.css"
function App(){
  const[query,setQuery]=useState("")
  const[weather,setWeather]=useState({})
  const change=(e)=>{
        setQuery(e.target.value)
  }
  const search=async(e)=>{
    if(e.code==="Enter"){
       let data=  await FetchWeather(query)
       setWeather(data)
    }
  }
  return(
    <div className="">
      <h2 style={{marginTop:"2%",textAlign:"center",color:"purple"}}>Weather App</h2>
      <div class="input-group mb-3" style={{marginTop:"2%"}}>
       <span class="input-group-text" id="inputGroup-sizing-default">Search Your City</span>
       <input type="text" value={query} onKeyPress={search}onChange={change} class="form-control"/>
       </div>
       <div class="card text-bg-dark mb-3" style={{maxWidth:"18rem",textAlign:"center"}}>
  <div class="card-header">Deatils</div>
  <div class="card-body">
    {weather.main && 
     <div className="weather_data">
      <div id="city">
        {weather.name} <span>{weather.sys.country}</span>
      </div>
      <div id="temp">
        {Math.round(weather.main.temp)}<span>&deg;C</span>
      </div>

     </div>
}
  </div>
</div>
    </div>
  )
}

export default App;
