import FetchWeather from "./FetchWeather";
import { useState } from "react";
import "./App.css"
function App(){
  const[query,setQuery]=useState("")
  const[weather,setWeather]=useState({})
  const change=(e)=>{
        setQuery(e.target.value)
  }
  const search=async(e)=>{
    if(e.code==="Enter"){
       let data=  await FetchWeather(query)
       setWeather(data)
    }
  }
  return(
    <div>
      <h2 style={{marginTop:"2%",textAlign:"center",color:"purple"}}>Weather App</h2>
      <label>Search Your City</label><br/>
      <input type="text" style={{marginTop:"2%",size:"50"}} value={query} onKeyPress={search}onChange={change}></input>
    {weather.main && 
     <div className="weather_data">
      <div id="city">
        {weather.name} <span>{weather.sys.country}</span>
      </div>
      <div id="temp">
        {Math.round(weather.main.temp)}<span>&deg;C</span>
      </div>
     </div>
}
    </div>
  )
}

export default App;
